$(function () {

    $('.search__input').on('input', $.debounce(sendAjax, 300));
    $('.search__input').closest();
    function sendAjax(){
        let $this = $('.search__input'),
            val = $this.val(),
            form = $this.closest('.search'),
            rep = form.find('.search__ajax'),
            data = form.serialize();

        if (val.length > 2) {
            $.ajax({
                dataType: "json",
                type: "POST",
                url: 'ajax.php',
                data: data,
                success: function (result) {
                    if (result.status) {
                        rep.html(result.html).addClass('active');
                    } else {
                        alert('Что-то пошло не так, попробуйте еще раз!!!');
                    }
                },
                error: function (result) {
                    alert('Что-то пошло не так, попробуйте еще раз!!!');
                }
            });
        } else {
            rep.removeClass('active');
        }
    }

    $('.search__clear').on('click', function (e) {
        let $this = $(this),
            form = $this.closest('.search');

        form.trigger('reset');
    });

    $('.search').on('reset', function (e) {
        let $this = $(this),
            ajax = $this.find('.search__ajax');
        ajax.removeClass('active');
    });

    let ispecsSwithes = document.querySelector('.ispecs__sws-list');
    if (ispecsSwithes) {

        let switches = new Switches({
            sws: '.ispecs__sws-list',
            sw: '.ispecs__sw',
            line: '.ispecs__subline',
            tab: '.ispecs__tab',
            autoWidth: true
        }).init();
    }

});
$(function () {
    let maxH = 0,
        tabs = $('.ispecs__tabs');
    $('.ispecs__tab').each(function (indx, elem) {
        let tab = $(elem),
            height = tab.outerHeight();

        if (maxH < height) {
            maxH = height;
        }
    });
    tabs.css('height', maxH + 'px');


    $('.count__up').on('click', function (e) {
        e.preventDefault();

        let $this = $(this),
            count = $this.closest('.count'),
            input = count.find('.count__input'),
            val = Number(input.val());
        val++;

        input.val(val);

    });

    $('.count__down').on('click', function (e) {
        e.preventDefault();

        let $this = $(this),
            count = $this.closest('.count'),
            input = count.find('.count__input'),
            val = Number(input.val());
        if (val>1) {
            val--;
        }

        input.val(val);

    });

});
$(function () {
    $('.select').styler();

    $('body').on('click', '.catalog__types .type', function (e) {
        let $this = $(this),
            form = $this.closest('form'),
            type = $this.attr('data-type'),
            input = form.find('input[name=type]');

        input.val(type);

        let data = form.serialize();

        sendAjax(form, data);
    });


    $('body').on('change', '.catalog__select-select .select', function (e) {
        let $this = $(this),
            form = $this.closest('form'),
            data = form.serialize();

        sendAjax(form, data);

    });

    function sendAjax(form, data) {
        $.ajax({
            dataType: "json",
            type: "POST",
            url: 'ajax.php',
            data: data,
            success: function (result) {
                if (result.status) {
                    form.replaceWith(result.html);
                    $('.select').styler();
                } else {
                    alert('Что-то пошло не так, попробуйте еще раз!!!');
                }
            },
            error: function (result) {
                alert('Что-то пошло не так, попробуйте еще раз!!!');
            }
        });
    }

});

